execution.script:
pipeline {
    agent any

    environment {
        imagename = "eroom-api-core"
        registryCredential = 'docker-hub'
        dockerImage = ''
    }

    stages {
        stage('Prepare') {
          steps {
            echo 'Clonning Repository'
            sh 'pwd'
            sh 'ls -al'
            sh 'cd ../eroom-api-docker/deploy'
            git url: 'https://github.com/e-room/e-room.git',
              branch: 'ci-cd-test',
              credentialsId: 'github'
            sh 'ls -al'
            }
            post {
             success { 
               echo 'Successfully Cloned Repository'
             }
           	 failure {
               error 'This pipeline stops here...'
             }
          }
        }

        stage('Bulid Gradle') {
          steps {
            echo 'Bulid Gradle'
            sh 'pwd'
            dir('./github_pull'){
                sh 'pwd'
                sh 'gradle clean'
                sh 'gradle bootJar'
            }
          }
          post {
            failure {
              error 'This pipeline stops here...'
            }
          }
        }

        stage('Bulid Docker') {
          steps {
            echo 'Bulid Docker'
            sh 'cd /eroom-api-docker/eroom-api-dockerfile'
            script {
                dockerImage = docker.build imagename
            }
          }
          post {
            failure {
              error 'This pipeline stops here...'
            }
          }
        }

        stage('Push Docker') {
          steps {
            echo 'Push Docker'
            script {
                docker.withRegistry( '', registryCredential) {
                    dockerImage.push() 
                }
            }
          }
          post {
            failure {
              error 'This pipeline stops here...'
            }
          }
        }

        stage('Docker Run') {
            steps {
                echo 'Pull Docker Image & Docker Image Run'
                sshagent (credentials: ['ssh']) {
                    sh 'cd /home/ubuntu/deploy'
                    sh "ssh -o StrictHostKeyChecking=no ubuntu@ec2-43-200-50-204.ap-northeast-2.compute.amazonaws.com 'docker pull eroom-api-core'" 
                    sh "ssh -o StrictHostKeyChecking=no ubuntu@ec2-43-200-50-204.ap-northeast-2.compute.amazonaws.com 'docker ps -q --filter name=eroom-api-compose | grep -q . && docker rm -f \$(docker ps -aq --filter name=eroom-api-compose)'"
                    sh "ssh -o StrictHostKeyChecking=no ubuntu@ec2-43-200-50-204.ap-northeast-2.compute.amazonaws.com 'docker run -d --name eroom-api-compose -p 8080:8080 eroom-api-core'"
                }
            }
        }
    }
    post {
        success {
            slackSend (channel: '#채널명', color: '#00FF00', message: "SUCCESSFUL: Job '${env.JOB_NAME} [${env.BUILD_NUMBER}]' (${env.BUILD_URL})")
        }
        failure {
            slackSend (channel: '#채널명', color: '#FF0000', message: "FAILED: Job '${env.JOB_NAME} [${env.BUILD_NUMBER}]' (${env.BUILD_URL})")
        }
    }
}
